import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import categorizeCrimeData from './categorizeCrimeData';
import neighborhoods from './neighborhoods';

// Fix for the default icon issue in Leaflet
delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

const MapComponent = () => {
  const [categorizedData, setCategorizedData] = useState([]);

  useEffect(() => {
    fetch('/api/crimeData')
      .then(response => response.json())
      .then(data => {
        const categorized = categorizeCrimeData(data);
        setCategorizedData(categorized);
      })
      .catch(error => console.error('Error loading crime data:', error));
  }, []);

  return (
    <MapContainer center={[1.3521, 103.8198]} zoom={12} style={{ height: "500px", width: "100%" }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      {categorizedData.map((neighborhood, idx) => (
        <React.Fragment key={idx}>
          {neighborhood.crimes.map((crime, crimeIdx) => (
            <Marker key={crimeIdx} position={[crime.lat, crime.lng]}>
              <Popup>
                {neighborhood.name}: Crime Weight {crime.weight}
              </Popup>
            </Marker>
          ))}
        </React.Fragment>
      ))}
    </MapContainer>
  );
};

export default MapComponent;
