import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import HeatmapLayer from './HeatmapLayer';
import L from 'leaflet';

// Fix for the default icon issue in Leaflet
delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

const MapComponent = () => {
  const [crimeData, setCrimeData] = useState([]);

  useEffect(() => {
    fetch('/api/crimeData')
      .then(response => response.json())
      .then(data => setCrimeData(data))
      .catch(error => console.error('Error loading crime data:', error));
  }, []);

  // Transform the data into the format required by HeatmapLayer
  const heatmapPoints = crimeData.map(crime => [crime.lat, crime.lng, crime.weight]);

  return (
    <MapContainer center={[1.3521, 103.8198]} zoom={12} style={{ height: "500px", width: "100%" }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <HeatmapLayer points={heatmapPoints} />
    </MapContainer>
  );
};

export default MapComponent;
